mapper.select:
select id, userid,password, name, email



from member
where id = #{id}

mapper.update:
update member
set userid=#{updateParam.userid},

    password=#{updateParam.password},

    name=#{updateParam.name},
    email=#{updateParam.email}
where id = #{id}

mapper.select:
select *
from member
where userid = #{userid} AND password = #{password}

select name
from member
where userid = #{userid}

select userid, name,email
from member
where userid = #{userid}

select userid
from member
where userid = #{userid}
