mapper.select:
select id, email, password, name
from member
where id = #{id}

mapper.update:
update member
set userid=#{updateParam.email},
    name=#{updateParam.password},
    email=#{updateParam.name}
where id = #{id}

mapper.select:
select id
from member
where email = #{email}

mapper.update:
update member
set payment = #{updateParam.payment}
where email = #{email}
